import React, {useEffect} from 'react';
import classes from './cockpit.module.css';


const Cockpit = (props) => {

    // use Effect executes whenever a dependencie change.
    // can use as many useEffect as i want, for different actions/readability

    // this will only execute the 1st time. Cus there are no dependencies specificed, no dependencies will change meaning this will never execute again
    // it executes on the 1st render and when unmounting/removing
    useEffect(() => {
        console.log("[Cockpit.js] useEffect - no dep");
        setTimeout(() => {
            alert("Saved data to the cloud");
        }, 1000);

        // this will run BEFORE the main useEffect function runs but AFTER the 1st render cycle
        return () => {
            console.log("[Cockpit.js] cleanup work in useEffect - no dep");
        };
    }, []);

    // just with the console.log it runs all the time. Combines componentDidMount and componentDidUpdate
    // no dependencie array means it will run for every update cycle
    useEffect(() => {
        console.log("[Cockpit.js] useEffect 1");
        setTimeout(() => {
            alert("Saved data to the cloud");
        }, 1000);
        return () => {
            console.log("[Cockpit.js] cleanup work in useEffect 1");
        };
    });

    let buttonClasses = "";
    if(props.showPersons)
        buttonClasses = classes.Red;

    const assignedClasses = [];
    if(props.persons.length <= 2) {
      assignedClasses.push(classes.red);
    }
    if(props.persons.length <= 1) {
      assignedClasses.push(classes.bold);
    }

    return (
        <div className={classes.Cockpit}>
            <h1>{props.appTitle}</h1>
            <p className={assignedClasses.join(" ")}>this is rly working</p>
            <button className={buttonClasses} onClick={props.clicked}>
            Toggle Persons
            </button>
        </div>
    );
}

export default Cockpit;
